mapper.update:
UPDATE MEMBER
SET
	PASSWORD=#{password}, NAME=#{name}, PHONE=#{phone}, EMAIL=#{email}
WHERE
	ID = #{id}

mapper.select:
SELECT ID, PASSWORD, NAME, NICKNAME, PHONE, EMAIL,
TO_CHAR(REGIST_DATE, 'YYYY-MM-DD hh24:mi') AS REGIST_DATE, FLAG_ADMIN	
FROM MEMBER WHERE ID=#{id}

